import styles from './Collection.module.scss'

function Collection({
  name,
  images,
  onClickIdx,
  onClickUrl,
  photoUrl,
  photoIdx,
}) {
  return (
    <div className={styles.collection}>
      <img
        className={styles.collectionBig}
        src={photoUrl === images[photoIdx] ? images[photoIdx] : images[0]}
        alt="Item"
      />
      <div className="collectionBottom">
        {images.map((value, index) => (
          <img
            onClick={() => {
              onClickIdx(index)
              onClickUrl(value)
            }}
            key={index}
            className="collection__mini"
            src={value}
            alt="Item"
          />
        ))}
      </div>
      <h4>{name}</h4>
    </div>
  )
}

export default Collection
